/**
 * ProductDetailsPage - Page Object Model for Product Details
 * Contains elements and methods for product details page interactions
 */
public class ProductDetailsPage extends BasePage {

    // Locators
    private final By sizeButtons = By.xpath("//div[@class='size-buttons-buttonContainer']");
    private final By addToBagButton = By.xpath("//div[text()='ADD TO BAG']");
    private final By productTitle = By.xpath("//h1[@class='pdp-title']");
    private final By productPrice = By.xpath("//span[@class='pdp-price']");
    private final By sizeNotSelectedError = By.xpath("//div[text()='Please select a size']");

    /**
     * Selects a product size (default: second size)
     */
    public void selectSize() {
        List<WebElement> sizes = wait.until(driver -> 
            driver.findElements(sizeButtons));

        if (!sizes.isEmpty()) {
            // Select the second available size
            int sizeIndex = sizes.size() > 1 ? 1 : 0;
            sizes.get(sizeIndex).click();
            logger.info("✓ Selected product size at index: {}", sizeIndex);
        } else {
            logger.warn("No size buttons found!");
        }
    }

    /**
     * Selects size by index
     */
    public void selectSizeByIndex(int index) {
        List<WebElement> sizes = wait.until(driver -> 
            driver.findElements(sizeButtons));

        if (sizes.size() > index) {
            sizes.get(index).click();
            logger.info("✓ Selected product size at index: {}", index);
        }
    }

    /**
     * Adds product to bag
     */
    public void addProductToBag() {
        String productName = getProductTitle();
        clickElement(addToBagButton);
        logger.info("✓ Product '{}' added to bag", productName);
    }

    /**
     * Gets product title
     */
    public String getProductTitle() {
        return getPageTitle();
    }

    /**
     * Gets product price
     */
    public String getProductPrice() {
        if (isElementDisplayed(productPrice)) {
            return getElementText(productPrice);
        }
        return "";
    }

    /**
     * Verifies if Add to Bag button is displayed
     */
    public boolean isAddToBagButtonDisplayed() {
        return isElementDisplayed(addToBagButton);
    }

    /**
     * Checks if size is selected
     */
    public boolean isSizeSelected() {
        return !isElementDisplayed(sizeNotSelectedError);
    }
}

